import { useTranslation } from 'react-i18next';
import { useState, useEffect } from 'react';
import { BaseButtonsForm } from '@app/components/common/forms/BaseButtonsForm/BaseButtonsForm';
import { InputNumber } from '@app/components/common/inputs/InputNumber/InputNumber';
import { Select, Option } from '@app/components/common/selects/Select/Select';
import { Button } from '@app/components/common/buttons/Button/Button';
import { RadioButton, RadioGroup } from '@app/components/common/Radio/Radio';
import { notificationController } from '@app/controllers/notificationController';
import { Alert } from '@app/components/common/Alert/Alert';

import { Card, Col, Row } from 'antd';
import styled from 'styled-components';



const ItalicText = styled.p`
  font-style: sans;
  font-size: 1.2rem;
`;

const formItemLayout = {
  labelCol: { span: 24 },
  wrapperCol: { span: 24 },
};

export const ClientSelectionForm: React.FC = () => {

  const [selectedMethod, setSelectedMethod] = useState<string | null>(null);
  const [isFieldsChanged, setFieldsChanged] = useState(false);
  const [isLoading, setLoading] = useState(false);
  const { t } = useTranslation();
  
  const onFinish = async (values = {}) => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setFieldsChanged(false);
      notificationController.success({ message: t('common.success') });
      console.log(values);
    }, 1000);

  };

  return (
    <BaseButtonsForm
      {...formItemLayout}
      isFieldsChanged={isFieldsChanged}
      onFieldsChange={() => setFieldsChanged(true)}
      name="validateForm"
      initialValues={{
        'input-number': 10,
        'checkbox-group': ['A', 'B'],
        rate: 3.5,
      }}
      footer={
        <BaseButtonsForm.Item>
          <Button type="primary" htmlType="submit" loading={isLoading}>
            {t('common.submit')}
          </Button>
        </BaseButtonsForm.Item>
      }
      onFinish={onFinish}
    >
      <BaseButtonsForm.Item
        name="select"
        label={t('Client Section Method')}
        hasFeedback
        rules={[{ required: true, message: t('You must choose client selection method!') }]}
      >
        <Select placeholder={t('Select client selection method')} onChange={ (value)=> setSelectedMethod(value as string)}>  
          <Option value="random">{t('Random')}</Option>
          <Option value="oort">{t('Oort')}</Option>
          <Option value="eafl">{t('EAFL')}</Option>
          <Option value="battery">{t('Battery life Optimization')}</Option>
        </Select>
      </BaseButtonsForm.Item>

      {/* Conditional rendering based on the selected method */}
      {selectedMethod === 'random' && <div> 
        <Card title={t('Random Method Configuration')}>
          Random selection method requires no additional settings
        </Card>
        {/* <Alert message={t('yap')} description={t('hehe')} type="info" showIcon />
        <Alert
            message={t('alerts.warning')}
            description={t('alerts.warningDescription')}
            type="warning"
            showIcon
            closable
          /> */}
        </div>}
      {selectedMethod === 'oort' && <div>
        <Card title={t('Oort Method Configuration')}>
          <Col span={15}>        
            <img src="/oort.png" alt="oort" />
          </Col>
          <Col span={10}>
            <span className="ant-form-text"> Client Score(i) = Util(i) </span>
          </Col>
          
          <Col span={10}>
            <BaseButtonsForm.Item label={t('Insert Value')}>
              <span className="ant-form-text"> &alpha;</span>
              <label>
                <BaseButtonsForm.Item name="client-number" rules={[{ required: true, message: t('Typing Client Number') }]} noStyle>
                  <InputNumber min={1} max={100} />
                </BaseButtonsForm.Item>
              </label>
            </BaseButtonsForm.Item>
          </Col>
        </Card>

        </div>}
      {selectedMethod === 'eafl' && <div> 
        <BaseButtonsForm.Item name="client-number" rules={[{ required: true, message: t('Typing Client Number') }]} noStyle>
            <InputNumber min={1} max={100} />
        </BaseButtonsForm.Item>
        </div>}
      {selectedMethod === 'battery' && <div> 
        Battery life Optimization specific components
        </div>}


      <BaseButtonsForm.Item 
        name="loss-button"
        // label={t('forms.validationFormLabels.radioButton')}
        label={t('Loss Function')}
        rules={[{ required: true, message: t('Choose Metric') }]}
      >
        <RadioGroup>
          <RadioButton value="mae">{t('MAE')}</RadioButton>
          <RadioButton value="mse">{t('MSE')}</RadioButton>
          <RadioButton value="rmse">{t('RMSE')}</RadioButton>
          <RadioButton value="binary">{t('Binary-CrossEntry')}</RadioButton>
        </RadioGroup>
      </BaseButtonsForm.Item>

      <BaseButtonsForm.Item
        name="opt-button"
        // label={t('forms.validationFormLabels.radioButton')}
        label={t('Optimaizer')}
        rules={[{ required: true, message: t('Choose loss Function') }]}
      >
        <RadioGroup>
          <RadioButton value="sgd">{t('SGD')}</RadioButton>
          <RadioButton value="adam">{t('Adam')}</RadioButton>
          <RadioButton value="adagrad">{t('Adagrad')}</RadioButton>
        </RadioGroup>
      </BaseButtonsForm.Item>
    </BaseButtonsForm>
  );
};
