import React from 'react';
import ReactApexChart from 'react-apexcharts';

const PowerConsumptionChart: React.FC = () => {
  const [state, setState] = React.useState({
    series: [
      {
        name: 'Power Consumption',
        data: [1.835532188,1.261049071,1.498732787,1.909634015,1.305707994,1.249845372],
      },
    ],
    options: {
      chart: {
        height: 350,
        type: 'bar' as const,
      },
      plotOptions: {
        bar: {
          borderRadius: 10,
          dataLabels: {
            position: 'top',
          },
        },
      },
      dataLabels: {
        enabled: true,
        formatter: function (val : any) {
          return val + "%";
        },
        offsetY: -20,
        style: {
          fontSize: '12px',
          colors: ["#304758"],
        },
      },
      xaxis: {
        categories: ["Client-1", "Client-2", "Client-3", "Client-4", "Client-5", "Client-6"],
        position: 'top',
        axisBorder: {
          show: false,
        },
        axisTicks: {
          show: false,
        },
        crosshairs: {
          fill: {
            type: 'gradient',
            gradient: {
              colorFrom: '#D8E3F0',
              colorTo: '#BED1E6',
              stops: [0, 100],
              opacityFrom: 0.4,
              opacityTo: 0.5,
            },
          },
        },
        tooltip: {
          enabled: true,
        },
      },
      yaxis: {
        axisBorder: {
          show: false,
        },
        axisTicks: {
          show: false,
        },
        labels: {
          show: false,
          formatter: function (val:any) {
            return val + "kW";
          },
        },
      },
      title: {
        text: 'Monthly Inflation in Argentina, 2002',
        floating: true,
        offsetY: 330,
        align: 'center' as any,
        style: {
          color: '#444',
        },
      },
    },
  });

  return (
    <ReactApexChart options={state.options} series={state.series} type="bar" height={400} />
  );
};

export default PowerConsumptionChart;
