import React, { useState, useEffect } from 'react';
import { useTheme } from 'styled-components';
import { useTranslation } from 'react-i18next';
import * as echarts from 'echarts';
import axios from 'axios';
import { BaseChart } from '@app/components/common/charts/BaseChart';
import { Card } from '@app/components/common/Card/Card';


export const BatteryStackedChart: React.FC = () => {
  const theme = useTheme();
  const { t } = useTranslation();

  const chartColors = theme.colors.charts;

  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios('data.json');
      setChartData(result.data);
    };

    fetchData();
  }, []);

  const option = {
    tooltip: {
      trigger: 'axis',
      axisPointer: {
        type: 'cross',
        label: {
          backgroundColor: chartColors.tooltipLabel,
        },
      },
    },
    legend: {
      data: [`coal`, `hydro`, `nuclear`, `gas`, `oil`].map((item) => t(`charts.${item}`)),
      top: 0,
      left: 10,
      textStyle: {
        color: theme.colors.text.main,
      },
    },
    grid: {
      top: 80,
      left: 20,
      right: 20,
      bottom: 0,
      containLabel: true,
    },
    xAxis: [
      {
        type: 'category',
        boundaryGap: false,
        data: chartData?.categories,
        axisLabel: {
          fontSize: theme.commonFontSizes.xxs,
          fontWeight: theme.commonFontWeight.light,
          color: theme.colors.main.primary,
        },
      },
    ],
    yAxis: [
      {
        type: 'value',
        name: 'TWh',
        axisLabel: {
          fontSize: theme.commonFontSizes.xxs,
          fontWeight: theme.commonFontWeight.light,
          color: theme.colors.text.main,
        },
      },
    ],
    series: chartData?.series.map((data) => ({
      data:data.value
    })),
  };

  return (
    <Card padding="0 0 1.875rem" title={t('charts.gradientLabel')}>
      <BaseChart option={option} />
    </Card>
  );
};
